import { useState } from "react";
function Category(props) {
    console.log(props.cproduct);
    const [selectedCategory, setSelectedCategory] = useState('');
    function handleSearch(){
        const category = document.getElementById('category').value;
        setSelectedCategory(category);
        props.setCat(category);
    }
    return (
        <div>
            <h1>Select Category</h1>
            <label htmlFor="category">Choose a category:</label>
            <select id="category" >
                <option value="">Select Category</option>
                <option value="dress">Dress</option>
                <option value="electronics">Electronics</option>
            </select>
            <button type="button" className="btn btn-primary me-md-2" onClick={handleSearch}>Search</button>
            
            <table className="t1 t3">
                <thead>
                <tr className="t1 t3">
                <th>Product Image</th><th>Product Name</th> <th>Category</th><th>Base Bidding</th><th>End Date</th>
                </tr>
                </thead>
                <tbody>
                {
                    props.cproduct.map(cp=>{
                        return(
                            <tr className="t2" key={cp._id}>
                                <td>Product image</td>
                                <td>Product Name</td>
                                <td>Category_Name</td>
                                <td>Base_Bid</td>
                                <td>Base_End</td>
                            </tr>
                        )
                    })    
                  }
                </tbody>
            </table>
        </div>
    );
}

export default Category;
