import Header from "./Component/Header";
import Footer from "./Component/Footer";
import Content from "./Component/Content";
import Category from "./Component/Category";
import { useEffect, useState } from "react";
function App()
{
    //get all poduct from db and display
    const [bidpro,setpro]=useState([])
    useEffect(()=>{
        getallpro();
    },[])
    const getallpro=async()=>{
        const res=await axios.get("http://127.0.0.1:4000/activelis")
        console.log(res.data.allproduct);
        setpro(res.data.allproduct);  
    }

    // category
    const [selectedCategory, setSelectedCategory] = useState('');
    useEffect(()=>{
        if(selectedCategory)
        handleCategoryChange();
    })
    const handleCategoryChange = (category) => {
        setSelectedCategory(category);

       
    };

    //posting data from frontend to database
    console.log(bidpro)
    const insertToDB=async (products)=>{
        const res=await axios.post("http://127.0.0.1:4000/create",products)
        console.log(res)
        getallpro();
    }
    return(
        <div>
            <Header pro={bidpro} insertToDB={insertToDB}></Header>
            
        </div>
    )
}
export default App;
