        console.log(res)
        const res1=await axios.post("http://127.0.0.1:4000/create/upload",products)
        getallpro();
    }
    return(
        <div>
            <Header pro={bidpro} insertToDB={insertToDB}></Header>
            
        </div>
    )
}
export default App;
