// Bid.js
import "./Bid.css";

function Bid(props) {
  console.log(props.pro);
  return (
    <div className="bid-container">
      {props.pro.map((s) => {
        return (
          <div className="bid-item" key={s._id}>
            <div className="image-container">
              <img src={`http://127.0.0.1:4000/${s.pimage}`} alt={s.product_name} />
            </div>
            <div className="details-container">
              <p><span className="bold">Product Name:</span> {s.product_name}</p>
              <p><span className="bold">Category:</span> {s.category_name}</p>
              <p><span className="bold">Base Bidding:</span> {s.base_bid}</p>
              <p><span className="bold">Bidding End Date:</span> {s.bid_end}</p>
              <input type="number" placeholder="Enter Bid Amount" className="bid-input" />
              <button className="place-bid-btn">Place Bid</button>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default Bid;
